import { createAction, handleActions } from 'redux-actions';


const initState = {
    value: 0,
};
  
const MENU_MEAL = "MENU_MEAL";
const MENU_GRAD = "MENU_GRAD";
const MENU_TOOL = "MENU_TOOL";

export const changeMenu = createAction(CHANGE_MENU, (value)=>({
  value,
}));


const reducer = handleActions(
  {
    [CHANGE_MENU]: (state, {payload: {value}})=>({
      ...state,
      value: value,
    })
  },
  initState
);

export default reducer;
