import style from 'styled-components';
import search from '../../assets/icon/search.svg';
import downward from '../../assets/icon/downward.svg';

const HeaderWrap = style.div`
    width: 100vw;
    height: 20vh;
    background-color: rgba(48, 119, 86, 1);
`;

const MarketSelectWrap = style.div`
    width: 100vw;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
`;

const MarketForm = style.form`

`;

const MarketSelect = style.select`
    width: auto;
    border: none;
    margin-top: 2rem;
    padding-left: 2rem;
    font-weight: bold;
    font-size: 1.2rem;
    color: rgba(248, 246, 250, 1);
    -webkit-appearance: none; /* 네이티브 외형 감추기 */ 
    -moz-appearance: none; 
    appearance: none;
    background: url(${downward}) no-repeat 1% 50%;
    background-size: 1.2rem 1.2rem;
    &:-ms-expand { 
        display: none; 
    }
    &:Click{

    }
`;

const MarketOption = style.option`
    font-size: 0.8rem;
    color: black;
`;

const DownwardImg = style.img`

`;

const SearchBarWrap = style.div`

`;

const SearchBar = style.input`
    -webkit-appearance: none; /* 네이티브 외형 감추기 */ 
    -moz-appearance: none; 
    appearance: none;
    width: 70vw;
`;

const SearchImg = style.img`

`;


const HeaderComponent = () => {
    return(
        <>
            <HeaderWrap>
                <MarketSelectWrap>
                    <MarketForm>
                        <MarketSelect>
                            <MarketOption>망원시장</MarketOption>
                            <MarketOption>석촌시장</MarketOption>
                            <MarketOption>가락시장</MarketOption>
                        </MarketSelect>
                    </MarketForm>
                </MarketSelectWrap>
                <SearchBarWrap>
                    <SearchBar />
                    <SearchImg />
                </SearchBarWrap>
            </HeaderWrap>
            {//<SearchImg src={search} />
            }
        </>
    );
}

export default HeaderComponent;
