import style from 'styled-components';
import React, {useState} from 'react';
import {useDispatch,useSelector} from 'react-redux';
import {menuMeal, menuGrad, menuTool} from '../../modules/MiddleMenuSelect';

const MenuWrap = style.div`
    display:flex;
    flex-direction:row;
    justify-content:space-around;
    align-items:center;
    width: 100vw;
    height: 10vw;
    box-shadow: 0vw 0vw 7vw 0 rgba(0, 0, 0, 0.2);
`;

const Menu = style.div`
    font-size: 0.9rem;
    font-weight: bold;
    width: 18vw;
    height: 7vw;
    margin-top: 3vw;
    text-align: center;
    color: ${ props => (props.isClick ? 'rgba(48, 119, 86, 1)' : '#000000')};
    border-bottom: ${ props => (props.isClick ? '0.1rem solid rgba(249, 210, 86, 1)' : 'none')};
`;

const MiddleMenuCompoent = () => {
    const [isMealClick, setIsMealClick] = useState(true);
    const [isGradClick, setIsGradClick] = useState(false);
    const [isToolClick, setIsToolClick] = useState(false);
    
    const dispatch = useDispatch();
    const {value} = useSelector(state => ({
        value: state.SelectMenu.value,
    }));

    const onHandleMealClick = (e) => {
        e.preventDefault();
        setIsMealClick(true);
        setIsGradClick(false);
        setIsToolClick(false);
        //dispatch(menuMeal(value));
    }
    const onHandleGradClick = (e) => {
        e.preventDefault();
        setIsMealClick(false);
        setIsGradClick(true);
        setIsToolClick(false);
        //dispatch(menuGrad(value));
    }
    const onHandleToolClick = (e) => {
        e.preventDefault();
        setIsMealClick(false);
        setIsGradClick(false);
        setIsToolClick(true);
        //dispatch(menuTool(value));
    }

    return(
        <>
            <MenuWrap>
                <Menu onClick={onHandleMealClick} isClick={isMealClick}>밀키트</Menu>
                <Menu onClick={onHandleGradClick} isClick={isGradClick}>식재료</Menu>
                <Menu onClick={onHandleToolClick} isClick={isToolClick}>조리도구</Menu>
            </MenuWrap>
        </>
    )
}

export default MiddleMenuCompoent;
